import React, { useCallback, useState } from 'react';
import { Form, Button, Input} from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import { ADD_POST_REQUEST } from '../reducers/post';



const PostForm = () => {
    const dispatch = useDispatch();
    const { text, setText} = useState('');
    const {imagePaths, isAddingPost} = useSelector(state => state.post);



    const onSubmit = useCallback(() => {
        dispatch({
            type: ADD_POST_REQUEST,
            data: {
                text,
            }
        })

    }, []);

    return (
        <Form style={{margin: '10px 0 20px'}} encType="multipart/form-data" onSubmit={onSubmitForm}>
                <Input.TextArea maxLength={140} placeholder="어떤 신기한 일이 있었나요?" value={text}/>
                <div>
                    <Input type ="file" multiple hidden />
                    <Button>이미지 업로드</Button>
                    <Button type="primary" style={{ float: 'right' }} htmlType="submit" isLoading={isAddingPost}>트위터</Button>
                </div>
                <div>
                    {imagePaths.map((v) => {
                        return(
                            <div key ={v} style={{display: 'inline-block'}}>
                                <img src={'http://localhost:3065'+v} style={{width:'200px'}} alt= {v} />
                                <div>
                                    <Button>제거</Button>
                                </div>
                            </div>
                        )
                    })}
                </div>
            </Form>
    )
}

export default PostForm;
